import { upload } from "@testing-library/user-event/dist/upload";
import React, { useEffect, useState } from "react";

const Search= () =>{

const [dat, setDat] = useState(null) ;
const [info ,setInfo ]= useState(null); 
const [preview, setPreview] = useState() ;

console.log(dat) ;
const getData = async ()=>{

     try{ 
        const data = new FormData();
       
        data.append("image_input", dat, dat.name);
        
        const options = {
            method: 'POST',
            headers: {
                'X-RapidAPI-Key': 'd5b7d1e676msh052563ca3875628p152f44jsnca03cd34b509',
                'X-RapidAPI-Host': 'face-recognition18.p.rapidapi.com'
            },
            body: data
        };
        
       const res=  await fetch('https://face-recognition18.p.rapidapi.com/recognize_face', options)
              const jsonres = await res.json(); 
              setInfo(jsonres);
        console.log(jsonres);
    
    }
        catch(err)
        {
            console.log(err);
        }
   
}


useEffect(()=>{
    if(dat)
    {
        const reader = new FileReader() ;

        reader.onloadend= ()=>{
            setPreview(reader.result) ;
        }
        reader.readAsDataURL(dat) ;
    }
    else
    {
setPreview(null);
    }
getData() ;
}, [dat]); 


    return (
<>
<div className="container">
    <h1>Upload file</h1>
    <input type="file" onChange={(e)=>setDat(e.target.files[0])}  name="img"/>
  
</div>
<div className="con">
    <div className="icon">
    <img src={preview} alt="" />
    </div>
    <div className="info">
<p>Emotion : {info.recognition_result[0].emotion}</p>
    </div>

</div>
</>

    )
}
export default Search ;
